import { Component } from '@angular/core';

interface Personaje {
  nombre: string;
  poder : number;
}

@Component({
  selector: 'app-main-page',
  templateUrl: './main-page.component.html',
})
export class MainPageComponent {

  nuevo: Personaje = {
    nombre: 'Trunks',
    poder: 14000
  }

  cambiarNombre( evento: any ) {
    console.log( event )
  }

  agregar() {
    console.log( this.nuevo );
  }  
}
<div class="row">
    <div class="col">
        <app-dbz-list></app-dbz-list>
    </div>
    <div class="col">

        <app-add-character></app-add-character>
        <h3>Agregar <small>{{nuevo.nombre}}</small></h3> 
        <hr>

        <form (ngSubmit)="agregar()">
            <input 
                type="text" 
                placeholder="Nombre"
                name="nombre"
                [(ngModel)]="nuevo.nombre"
            />

            <!-- [value]="nuevo.nombre"
                {input}="cambiarNombre($event)"-->

            <input 
                type="number" 
                placeholder="Poder" 
                name="poder"
                [(ngModel)]="nuevo.poder"
            />

            <button>
                Agregar
            </button>
        </form>

    </div>
</div>
