import { Component } from '@angular/core';

@Component({
  selector: 'app-listado',
  templateUrl: './listado.component.html',
})
export class ListadoComponent {
  heroes: string[] = [ 'Spiderman', 'Iroman', 'Hulk'];

  heroeborrado: string = '';

  borrarHeroe() {
    this.heroeborrado = this.heroes.shift() || '';
  }
}
