import { Component } from '@angular/core';

interface Personaje {
  nombre: string;
  poder : number;
}

@Component({
  selector: 'app-main-page',
  templateUrl: './main-page.component.html',
})
export class MainPageComponent {

  nuevo: Personaje = {
    nombre: 'Trunks',
    poder: 14000
  }

  cambiarNombre( evento: any ) {
    console.log( event )
  }

  agregar() {
    console.log( this.nuevo );
  }  
}
<div class="row">
    <div class="col">
        <h3>Dragon Ball Z</h3>
        <hr>
        <ul>
            <li>Krillin - 700</li>
            <li>Goku - 15,000</li>
            <li>Vegeta - 8,500</li>
        </ul>
    </div>
    <div class="col">
        <h3>Agregar</h3> 
        <hr>

        <form (ngSubmit)="agregar()">
            <input 
                type="text" 
                placeholder="Nombre"
                name="nombre"
                [()]
            />
            <input 
                type="number" 
                placeholder="Poder" 
                [value]="nuevo.poder"
            />

            <button>
                Agregar
            </button>
        </form>

    </div>
</div>
